import React, { Component } from 'react';

const images = [
  "https://images.unsplash.com/photo-1567653418876-5bb0e566e1c2?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=800&q=60",
  "https://images.unsplash.com/photo-1534118650230-cec0aa21f255?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=626&q=80",
  "https://images.unsplash.com/photo-1532117364815-720cd35ff6e3?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1100&q=80",
  "https://images.unsplash.com/photo-1525059337994-6f2a1311b4d4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=708&q=80"
]

class Content extends Component {
  constructor(props) {
    super(props);

    this.state = {
      imageSrc : ""
    }

    this.pickImage = this.pickImage.bind(this);
  }

  pickImage() {
    this.setState({
      imageSrc: images[Math.floor(Math.random()*images.length)]
  })
}


  render () {
    return (
      <img src={this.state.imgSrc}/>
    )
  }
}

export default Content;
