import React, { Component } from 'react';
import Lollipop from './assets/pink-lollipop.jpeg';
import Suckers from './assets/colorful-lollipops.jpeg';
import Donuts from './assets/donuts-milk.jpeg'

class Content extends Component {
  constructor(props) {
    super(props);

    this.state = {
      imageSrc : [
        Lollipop,
        Suckers,
        Donuts
      ]
    }

    this.randomizer = this.randomizer.bind(this);

  }

  randomizer() {
    return Math.floor(Math.random()*this.state.imageSrc.length);
  }

  render () {
    return (
      <img src={this.state.imgSrc[this.randomizer()]}/>
    )
  }
}

export default Content;
